(1498, '2025-04-19', 'The Skinwalker Tile Picture Challenge', 'Welcome to The Skinwalker Tile Picture Challenge, a classic puzzle game where players must slide tiles to reassemble a picture. Move the blocks by touching or clicking on them to solve the puzzle.\n\nIn The Skinwalker Tile Picture Challenge, players are challenged to solve the puzzle by sliding tiles to reassemble a picture. The objective of the game is to move the blocks by touching or clicking on them until the picture is complete. Welcome to The Skinwalker Tile Picture Challenge and enjoy solving the puzzle.', '', 'Puzzle', 'gamepix', 'html5', 'https://games.assets.gamepix.com/L5SWI/thumbnail/small.png', 'https://games.assets.gamepix.com/L5SWI/thumbnail/medium.png', '', 'https://play.gamepix.com/the-skinwalker-tile-picture-challenge/embed?sid=1', '320', '480', '', 0, 0, 0, 'the-skinwalker-tile-picture-challenge', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/L5SWI\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/L5SWI\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1499, '2025-04-19', 'Robot Memory Match', 'Welcome to Robot Memory Match, a classic puzzle game where players must slide tiles to reassemble a picture. Move the blocks by touching or clicking on them to solve the puzzle.\n\nIn Robot Memory Match, players are challenged to solve the puzzle by sliding tiles to reassemble a picture. The objective of the game is to move the blocks by touching or clicking on them until the picture is complete. Welcome to Robot Memory Match and enjoy solving the puzzle.', '', 'Puzzle', 'gamepix', 'html5', 'https://games.assets.gamepix.com/HYECM/thumbnail/small.png', 'https://games.assets.gamepix.com/HYECM/thumbnail/medium.png', '', 'https://play.gamepix.com/robot-memory-match/embed?sid=1', '320', '480', '', 0, 0, 0, 'robot-memory-match', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/HYECM\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/HYECM\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1500, '2025-04-19', 'Weather Jigsaw Puzzle Frenzy', 'Welcome to Weather Jigsaw Puzzle Frenzy! Our Weather Jigsaw Puzzle Frenzy Puzzle Slider game is a fun and engaging way to test your visual acuity and problem-solving skills. Simply slide the images around until you find the perfect match, and watch as the puzzle pieces come together to form a beautiful Weather Jigsaw Puzzle Frenzy picture. With a wide variety of Weather Jigsaw Puzzle Frenzy images to choose from, you\'re sure to find something you love. So why wait? Come on in and start playing our Weather Jigsaw Puzzle Frenzy Puzzle Slider game today!\n\n', '', 'Puzzle', 'gamepix', 'html5', 'https://games.assets.gamepix.com/ZAGG1/thumbnail/small.png', 'https://games.assets.gamepix.com/ZAGG1/thumbnail/medium.png', '', 'https://play.gamepix.com/weather-jigsaw-puzzle-frenzy/embed?sid=1', '320', '480', '', 0, 0, 0, 'weather-jigsaw-puzzle-frenzy', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/ZAGG1\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/ZAGG1\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1501, '2025-04-19', 'Sky Blitz', 'This game is Sky Blitz, a simple arcade game you can play anywhere.', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/TL145/thumbnail/small.png', 'https://games.assets.gamepix.com/TL145/thumbnail/medium.png', '', 'https://play.gamepix.com/sky-blitz/embed?sid=1', '320', '480', '', 0, 0, 0, 'sky-blitz', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/TL145\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/TL145\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1502, '2025-04-19', 'Autumn Coloring Seasons Pages', 'Discover the joy of digital art with Autumn AI - Color Seasons, where each stroke adds color to your world. This interactive coloring book provides an array of thematic coloring pages suitable for all. It\'s more than a game; it\'s a journey through a spectrum of scenes and motifs where your paint choices make all the difference. Indulge in the art of coloring with Autumn AI - Color Seasons!\n\n', '', 'Junior', 'gamepix', 'html5', 'https://games.assets.gamepix.com/UGIAG/thumbnail/small.png', 'https://games.assets.gamepix.com/UGIAG/thumbnail/medium.png', '', 'https://play.gamepix.com/autumn-coloring-seasons-pages/embed?sid=1', '320', '480', '', 0, 0, 0, 'autumn-coloring-seasons-pages', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/UGIAG\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/UGIAG\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1503, '2025-04-19', 'Granny Jigsaw Puzzle', 'Granny Jigsaw Puzzle is a fun horror puzzle game. You can select one of the nine images and then select one of the four modes (16, 36, 64 and 100 pieces). Select your favorite picture and complete the jigsaw in the shortest time possible! Have fun and enjoy!', '', 'Puzzle', 'gamepix', 'html5', 'https://games.assets.gamepix.com/ENRLP/thumbnail/small.png', 'https://games.assets.gamepix.com/ENRLP/thumbnail/medium.png', '', 'https://play.gamepix.com/granny-jigsaw-puzzle/embed?sid=1', '480', '320', '', 0, 0, 0, 'granny-jigsaw-puzzle', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/ENRLP\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/ENRLP\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1504, '2025-04-19', 'Macagi Adventures', 'Macagi Adventures is an online game that you can play in modern browsers for free.', '', 'Adventure', 'gamepix', 'html5', 'https://games.assets.gamepix.com/57A50/thumbnail/small.png', 'https://games.assets.gamepix.com/57A50/thumbnail/medium.png', '', 'https://play.gamepix.com/macagi-adventures/embed?sid=1', '480', '320', '', 0, 0, 0, 'macagi-adventures', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/57A50\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/57A50\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1505, '2025-04-19', 'Noob vs Stickman Zombies', 'Noob vs Stickman Zombies one quiet summer morning, while Noob was still sleeping sweetly in his cozy cabin, someone turned on deafening music that made the whole house shake. When he came outside, he discovered that right next door to him, a group of Stickmen were having a fierce dance party, as if on purpose trying to annoy him. In a fit of rage, Noob removed his bow from the wall to avenge his ruined day off and teach all those who dared to disturb his peace! \"Noob vs Stickman Zombies\" is a fun arcade game with an interesting storyline in which you have to teach the annoying neighbors a lesson!', '', 'Arcade,Puzzle', 'gamepix', 'html5', 'https://games.assets.gamepix.com/4NOI1/thumbnail/small.png', 'https://games.assets.gamepix.com/4NOI1/thumbnail/medium.png', '', 'https://play.gamepix.com/noob-vs-stickman-zombies/embed?sid=1', '320', '480', '', 0, 0, 0, 'noob-vs-stickman-zombies', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/4NOI1\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/4NOI1\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1506, '2025-04-19', 'Stick Merge', 'Stick Merge is a simple action and merging game. You aim to combine various weapons to create more powerful guns, and ultimately use them on the moving stick figures in the shooting range. Prepare your arsenal, upgrade your pistol, explore all the power-ups and become the best shooter in town. How powerful can you get in Stick Merge? Stick around to find out.', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/ER5R5/thumbnail/small.png', 'https://games.assets.gamepix.com/ER5R5/thumbnail/medium.png', '', 'https://play.gamepix.com/mr-driller-mini/embed?sid=1', '320', '480', '', 0, 0, 0, 'stick-merge', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/ER5R5\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/ER5R5\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1507, '2025-04-19', 'Crowd Enhance', '\"Crowd Enhance\" is a casual arcade game in which you have to lead a squad of armed infantry to destroy all the enemies in your way! There are plenty of exciting levels ahead of you, in which you have to overcome various dangerous obstacles and fight against hordes of enemies trying to stop you.', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/46E63/thumbnail/small.png', 'https://games.assets.gamepix.com/46E63/thumbnail/medium.png', '', 'https://play.gamepix.com/crowd-enhance/embed?sid=1', '320', '480', '', 0, 0, 0, 'crowd-enhance', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/46E63\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/46E63\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
