(1544, '2025-04-19', 'Turbo Cars Challenge', 'Take control of high-speed racing action right in the palm of your hand with our thrilling mobile game! Navigate your car through intense arena races, where every turn and every drift counts. Win heart-pounding duels against fierce opponents, showcasing your skills and dominating the competition\n\n\nUse arrow keys on keyboard to steer your car', '', 'Sports', 'gamepix', 'html5', 'https://games.assets.gamepix.com/REB1H/thumbnail/small.png', 'https://games.assets.gamepix.com/REB1H/thumbnail/medium.png', '', 'https://play.gamepix.com/turbo-cars-challenge/embed?sid=1', '480', '320', '', 0, 0, 0, 'turbo-cars-challenge', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/REB1H\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/REB1H\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(1545, '2025-04-19', 'Mr. Macagi Adventures', 'Embark on a daring expedition through the whimsical landscapes of Mr. Macagi\'s Adventure! \n\nGuide Mr. Macagi through perilous terrains, braving cunning monsters. With 4 stages set amidst the dense foliage of the forest and 4 in the unforgiving heat of the desert, prepare for a test of skill and strategy.\n\nCan you help Mr. Macagi collect apples and gold coins, and outsmart the cunning monsters that inhabit them? Explore, strategize, and conquer in this thrilling platformer adventure!', '', 'Adventure', 'gamepix', 'html5', 'https://games.assets.gamepix.com/A56SA/thumbnail/small.png', 'https://games.assets.gamepix.com/A56SA/thumbnail/medium.png', '', 'https://play.gamepix.com/mr-macagi-adventures/embed?sid=1', '480', '320', '', 0, 0, 0, 'mr-macagi-adventures', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/A56SA\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/A56SA\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
