(2029, '2025-04-19', 'Skibidi Toilet Clicker', 'This is madness carzy and funny clicker game about skibidi toilets!!\n\nGreat skibidi toilet adventure\n\n\nClick to get points!!!!\nBuy cool upgrades!!', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/CBLTK/thumbnail/small.png', 'https://games.assets.gamepix.com/CBLTK/thumbnail/medium.png', '', 'https://play.gamepix.com/skibidi-toilet-clicker/embed?sid=1', '480', '320', '', 0, 0, 0, 'skibidi-toilet-clicker', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/CBLTK\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/CBLTK\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2030, '2025-04-19', 'Assault on the Evil Star', 'Assault on the Evil Star is a browser scifi third person shooter inspired by games  like Halo and Doom.\n\nStory\n\nThe story is about a special forces soldier who finds out about a big conspiracy to destroy humanity and tries to save people. He encounters enemy soldiers and scary monsters on his adventure.', '', 'Adventure', 'gamepix', 'html5', 'https://games.assets.gamepix.com/A12O1/thumbnail/small.png', 'https://games.assets.gamepix.com/A12O1/thumbnail/medium.png', '', 'https://play.gamepix.com/assault-on-the-evil-star/embed?sid=1', '480', '320', '', 0, 0, 0, 'assault-on-the-evil-star', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/A12O1\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/A12O1\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2031, '2025-04-19', 'Boneless', 'A poor man with just one arm left tries to escape from alien monsters and survive with limited ammo. How long you can survive against growing army of alien monsters, while they getting stronger and crowded each wave?\n\nUpgrade your bullets\' damage, your health and movement speed with coins that you gain when you killed an animal monster each time!\n', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/1LB11/thumbnail/small.png', 'https://games.assets.gamepix.com/1LB11/thumbnail/medium.png', '', 'https://play.gamepix.com/boneless/embed?sid=1', '480', '320', '', 0, 0, 0, 'boneless', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/1LB11\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/1LB11\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2032, '2025-04-19', 'Watermelon Pang Pang', 'Merge Fruits to Watermelon!\nPlay a fruit-matching game, where you collide identical fruits to evolve them and keep them from falling out of the box. But here\'s the twist: when the same fruits collide, they transform into an entirely new fruit. Can you merge your way to the grand watermelon?\n\nStrategically match fruits, and aim to become the ultimate Fruit Pang Pang. Keep the fruits from spilling out and challenge your mind with dynamic transformations.\n\nRemember, every merge counts! \n\nJoin the Ultimate Fruitful Adventure Now!', '', 'Puzzle', 'gamepix', 'html5', 'https://games.assets.gamepix.com/M7PGR/thumbnail/small.png', 'https://games.assets.gamepix.com/M7PGR/thumbnail/medium.png', '', 'https://play.gamepix.com/watermelon-pang-pang/embed?sid=1', '320', '480', '', 0, 0, 0, 'watermelon-pang-pang', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/M7PGR\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/M7PGR\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2033, '2025-04-19', 'Backrooms Skibidi Escape', 'Backrooms Skibidi Escape is a horror game where you are trapped in the backrooms chased by skibidi toilet and your mission is to find the exit or given number of video tapes to successfully complete each level.', '', 'Adventure', 'gamepix', 'html5', 'https://games.assets.gamepix.com/MR3SA/thumbnail/small.png', 'https://games.assets.gamepix.com/MR3SA/thumbnail/medium.png', '', 'https://play.gamepix.com/backrooms-skibidi-escape/embed?sid=1', '480', '320', '', 0, 0, 0, 'backrooms-skibidi-escape', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/MR3SA\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/MR3SA\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2034, '2025-04-19', 'Hotel Manager', 'Have you ever dreamt of being one of the biggest landowners in the world?\n\nGet your (virtual) chance in this extremely addictive game!\n\nBuild hotels all over the world, upgrade them and crowd your opponents out of the market, leaving their customers to come to you!', '', 'Board', 'gamepix', 'html5', 'https://games.assets.gamepix.com/H031A/thumbnail/small.png', 'https://games.assets.gamepix.com/H031A/thumbnail/medium.png', '', 'https://play.gamepix.com/hotel-manager/embed?sid=1', '480', '320', '', 0, 0, 0, 'hotel-manager', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/H031A\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/H031A\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2035, '2025-04-19', 'Farm Life', 'Farm Life is a casual game that immerses you in a serene and delightful countryside experience.  Within this game, you\'ll engage in a variety of activitiesplanting, harvesting, breeding, and managing your very own farm.\n\nPlanting and Trading\nCultivate crops to nurture and expand your farm!  After harvesting wheat, tomatoes, and more, head to the market to trade with fellow players, selling your produce and building your envisioned dream farm!\n\nProduction Plant\nWithin your own production facility, process different crops into refined industrial products for trading, securing higher returns on your investments.\n\nCollection and Decoration\nThe farm\'s cottage brims with charming furniture and unlockable decorative sets, enabling you to style your farm to reflect your unique taste!  Cottages can generate passive income and expedite the growth of your farm!\n\nIn Farm Life, relish the tranquility of country living as you delve into a tapestry of agricultural pursuits, trade, and creative farm development!\"', '', 'Strategy,Classics', 'gamepix', 'html5', 'https://games.assets.gamepix.com/F3617/thumbnail/small.png', 'https://games.assets.gamepix.com/F3617/thumbnail/medium.png', '', 'https://play.gamepix.com/farm-life/embed?sid=1', '320', '480', '', 0, 0, 0, 'farm-life', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/F3617\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/F3617\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1);
