(2255, '2025-04-19', 'Squid Shapes', 'In this game you must switch between the three possible shapes (circle, triangle, square) that are contained in the lower circle.\n\nOther shapes will descend through the center of the screen and as they meet the lower figure they must match!', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/7926S/thumbnail/small.png', 'https://games.assets.gamepix.com/7926S/thumbnail/medium.png', '', 'https://play.gamepix.com/squid-shapes/embed?sid=1', '320', '480', '', 0, 0, 0, 'squid-shapes', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/7926S\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/7926S\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2256, '2025-04-19', 'Train Battle', 'Place Tracks. Outrun your opponent. Make their train explode\n\nA deceptively simple strategy game. Think a turn-based version of the light bikes from Tron, a game of go with train tracks, or the love child of chess and centipede.', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/0N07R/thumbnail/small.png', 'https://games.assets.gamepix.com/0N07R/thumbnail/medium.png', '', 'https://play.gamepix.com/train-battle/embed?sid=1', '480', '320', '', 0, 0, 0, 'train-battle', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/0N07R\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/0N07R\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
