(2260, '2025-04-19', 'Stone Pile', 'stone pile is an html5 arcade game, stack the stones according to color otherwise the stones will be destroyed and you will die, stack them to the top and get the highest score', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/0P0T2/thumbnail/small.png', 'https://games.assets.gamepix.com/0P0T2/thumbnail/medium.png', '', 'https://play.gamepix.com/stone-pile/embed?sid=1', '320', '480', '', 0, 0, 0, 'stone-pile', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/0P0T2\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/0P0T2\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2261, '2025-04-19', 'Cute Cat Jigsaw Puzzle', 'Are you a cat lover with a passion for puzzles? If so, get ready for an adorable and engaging adventure with Cute Cat Jigsaw Puzzle. This delightful game features 15 levels of feline-themed fun that will captivate your heart and challenge your mind.', '', 'Junior', 'gamepix', 'html5', 'https://games.assets.gamepix.com/LZZ00/thumbnail/small.png', 'https://games.assets.gamepix.com/LZZ00/thumbnail/medium.png', '', 'https://play.gamepix.com/cute-cat-jigsaw-puzzle/embed?sid=1', '320', '480', '', 0, 0, 0, 'cute-cat-jigsaw-puzzle', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/LZZ00\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/LZZ00\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2262, '2025-04-19', 'Thanksgiving Squad Style', 'Step into the world of Thanksgiving Squad Style! Explore a fusion of cozy elegance and festive fashion as the squad celebrates the season. From warm layers to stylish accents, discover the perfect ensemble to make this Thanksgiving unforgettable. Join the squad and embrace the spirit of gratitude and style! ', '', 'Junior', 'gamepix', 'html5', 'https://games.assets.gamepix.com/I70S0/thumbnail/small.png', 'https://games.assets.gamepix.com/I70S0/thumbnail/medium.png', '', 'https://play.gamepix.com/thanksgiving-squad-style/embed?sid=1', '480', '320', '', 0, 0, 0, 'thanksgiving-squad-style', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/I70S0\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/I70S0\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2263, '2025-04-19', 'BFFs Black Friday Collection', 'Welcome to BFFs Black Friday Collection. Bffs already filled the closet with a new Black Friday outfit. Help them to pick the perfect costume. Dont forget to pick a fabulous hairstyle. They are all excited like us for this day. Join and help the girls with your skill. Play and have fun! ', '', 'Junior', 'gamepix', 'html5', 'https://games.assets.gamepix.com/20OBL/thumbnail/small.png', 'https://games.assets.gamepix.com/20OBL/thumbnail/medium.png', '', 'https://play.gamepix.com/bffs-black-friday-collection/embed?sid=1', '480', '320', '', 0, 0, 0, 'bffs-black-friday-collection', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/20OBL\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/20OBL\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2264, '2025-04-19', 'Frozen Sisters Dream Wedding', 'Welcome to the Frozen Sisters Dream Wedding. Princesses are more excited about their dream day with their beloved ones. Yes, Its their wedding. Help them to get ready for this occasion. And make this occasion a more memorable one in her life! Browse their wardrobe and pick suitable outfits and costumes. Help them to decorate the cake and invitation card. Join them and have fun!', '', 'Junior', 'gamepix', 'html5', 'https://games.assets.gamepix.com/7EN7S/thumbnail/small.png', 'https://games.assets.gamepix.com/7EN7S/thumbnail/medium.png', '', 'https://play.gamepix.com/frozen-sisters-dream-wedding/embed?sid=1', '480', '320', '', 0, 0, 0, 'frozen-sisters-dream-wedding', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/7EN7S\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/7EN7S\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2265, '2025-04-19', 'Ellie Thanksgiving Day', 'Welcome to Ellie Thanksgiving Day. Its Thanksgiving Day, and like every year, young Ellie is in charge of the Thanksgiving dinner. Her friends will start arriving soon, and there are still a few things that need her attention. Will you help her with the last preparations? Join and help beautiful Ellie to surprise her guests with a perfect dinner!', '', 'Junior', 'gamepix', 'html5', 'https://games.assets.gamepix.com/2A1V1/thumbnail/small.png', 'https://games.assets.gamepix.com/2A1V1/thumbnail/medium.png', '', 'https://play.gamepix.com/ellie-thanksgiving-day/embed?sid=1', '480', '320', '', 0, 0, 0, 'ellie-thanksgiving-day', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/2A1V1\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/2A1V1\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
(2266, '2025-04-19', 'Hungry Plant', 'In Hungry Plant you are a flying monster that wants to eat the magical seeds that grows inside a dangerous plant. But the plant is hungry too, and wants your fresh blood. Try to catch as many seeds as you can without getting bitten by the carnivorous plant!', '', 'Arcade', 'gamepix', 'html5', 'https://games.assets.gamepix.com/N0NNN/thumbnail/small.png', 'https://games.assets.gamepix.com/N0NNN/thumbnail/medium.png', '', 'https://play.gamepix.com/hungry-plant/embed?sid=1', '320', '480', '', 0, 0, 0, 'hungry-plant', '{\"thumbnail_source\":{\"thumb_1\":\"https:\\/\\/games.assets.gamepix.com\\/N0NNN\\/thumbnail\\/small.png\",\"thumb_2\":\"https:\\/\\/games.assets.gamepix.com\\/N0NNN\\/thumbnail\\/medium.png\"}}', 1, NULL, NULL, NULL, 1),
